# Membuat container dan bind data di dalam folder (bind mounts, namun tidak direkomendasikan)
docker container create --name mongodata --publish 27018:27017 --mount "type=bind,source=/home/alvi/Desktop/learn-docker/mongo-data,destination=/data/db" --env MONGO_INITDB_ROOT_USERNAME=alvi --env MONGO_INITDB_ROOT_PASSWORD=alvi mongo:latest